import React from 'react';
import ErrorMessageLcc from 'views/components/display/ErrorMessage';
import HintLcc from 'views/components/display/Hint';
import HeaderLarge from '../display/HeaderLarge';
import Label from '../display/Label';
import QuestionLabel from '../display/QuestionLabel';

export default class TextareaInputLcc extends React.Component {
  render() {
    return (
      <>
      <QuestionLabel qnData={this.props.qnData} qnCount={this.props.qnCount} />

        {this.props.errors.map((error) => {
          return <ErrorMessageLcc errorMessage={error.errorMessage} />;
        })
        }
        <textarea className={`form-control ${this.props.errors.length > 0 ? "form-control-error" : ""}`} id={this.props.qnData.name} name={this.props.qnData.name} rows={this.props.qnData.rows} cols={this.props.qnData.cols} required={this.props.qnData.isRequired} ></textarea>
      </>
    );
  }
}
