import React from 'react';
import withParamsAndNavigate from "helpers/withParamsAndNavigate";

class PageBase extends React.Component {
  render() {
    let formJson = require("../../forms/" + this.props.params.form + "/Config");
    let category = formJson.categories.find(category => category.name == "Personal information");
    let question = category.questions.find(question => question.name == "Name");

    switch (question.type) {
      case "Text":
        return(
        <>
          <label className='form-label'>{question.friendlyName}</label>
          <input></input>
        </>)
        break;

      default:
        break;
    }

    return;
  }
}

export default withParamsAndNavigate(PageBase);
