import React from "react";
import NavTile from 'Views/Components/NavTile';
import getCategoryList from 'Data/Categories';
import useParamsAndNavigate from "Helpers/useParamsAndNavigate";

export default useParamsAndNavigate(FormList);

class FormList extends React.Component {
  render() {
    let categories = getCategoryList();
    let category = categories.categories.find(element => element.name === this.props.params.category);

    return (
      <>
        <div className="text">
          <h1>{category.friendlyName}</h1>
        </div>

        <div className="groupPromo">
          <div className="listWebpart">
            <ul>
              {
                category.forms.map((form) => {
                  let route = "/" + form.name + "/Description";
                  return (
                    <li key={form.name}>
                      <NavTile to={route} header={form.friendlyName} description={form.description} />
                    </li>
                  );
                })
              }

            </ul>
          </div>
        </div>
      </>
    );
  }
}
