import React from 'react';
import ErrorMessageLcc from 'views/components/display/ErrorMessage';
import HintLcc from 'views/components/display/Hint';

export default class CheckboxInputLcc extends React.Component {
  render() {
    return (
      <>
        <HintLcc hint={this.props.qnData.hint} />

        <ErrorMessageLcc errors={this.props.errors} />

        <label className="block-label" htmlFor={this.props.name}>
          <input type="checkbox" id={this.props.name} name={this.props.name} value={this.props.value} required={this.props.isRequired} />
          <span className="form-label-bold">{this.props.displayName}</span>
        </label>
      </>
    );
  }
}
