import React from 'react';
import HeaderLarge from './HeaderLarge';
import Label from './Label';

export default class QuestionLabel extends React.Component {
  render() {
    const LabelElement = this.props.qnCount > 1
      ? <Label name={this.props.name} displayName={this.props.displayName} />
      : <HeaderLarge name={this.props.name} displayName={this.props.displayName} />;

    return (
      <>
        {this.props.useLegend
          ? <LabelElement />
          : <legend>
            <LabelElement />
          </legend>
        }

        <HintLcc hint={this.props.hint} />
      </>
    );
  }
}
