import React from 'react';
import { Navigate } from "react-router-dom";
import getContactUsNameErrorList from 'data/ContactUs-Name-ErrorList';
import withRouterHooks from 'helpers/withRouterHooks';

import TextInputLcc from 'views/components/inputs/Text';
import MultiCheckboxInputLcc from 'views/components/inputs/MultiCheckbox';
import RadioInputLcc from 'views/components/inputs/Radio';
import TextareaInputLcc from 'views/components/inputs/Textarea';
import DateInputLcc from 'views/components/inputs/Date';
import TimeInputLcc from 'views/components/inputs/Time';
import FileUploadInputLcc from 'views/components/inputs/FileUpload';
import DropdownInputLcc from 'views/components/inputs/Dropdown';
import CheckboxInputLcc from 'views/components/inputs/Checkbox';
import ParagraphLcc from 'views/components/inputs/Paragraph';
import ErrorSummaryLcc from 'views/components/display/ErrorSummary';
import HeaderLarge from 'views/components/display/HeaderLarge';

import getContactUsServiceRequest from 'data/ContactUs-ServiceRequest';

class PageBase extends React.Component {
    constructor(props) {
        super(props);
        //If controlled form will need all fields from json
        this.state = { FirstName: '', Surname: ''};
        this.onValueChange = this.onValueChange.bind(this);
    }
    //This will probably move
    onValueChange(e) {
        //Lifting up - top level
        const target = e.target
        const value = target.type === 'checkbox' ? target.checked : target.value
        const name = target.name
        this.setState({
            [name]: value
        })
    }
    render() {
        //const [name] = this.props.outletContext;

        // Can't do relative paths here, only with imports at the top of files :(
        // .default takes the default export from the file
        //Check if specific page first, if not match params.page, if not error
        let FormPage;
        let thisPageData = [];
        if (this.props.params.page === 'Summary') {
            FormPage = require("../shared/Summary").default;
        }
        //Need check for reusable pages
        else if (this.props.params.page === 'Description') {
            FormPage = require("../../forms/" + this.props.params.form + "/" + this.props.params.page).default;
        }
        else {
            //Call a lambda instead here where pass form name and page from params
            let contactUsServiceRequest = getContactUsServiceRequest();
            thisPageData = contactUsServiceRequest.formViews.filter(f => f.name === this.props.params.page);
            if (thisPageData.length > 0) {
                FormPage = require("../shared/DynamicPage").default;
            }
            else {
                console.log("nav to error page");
                //this.props.navigate("/ContactUsPathways/Generic");
            }
        }
        return <FormPage pageData={thisPageData[0]} /*outletProps={name}*/ onValueChange={this.onValueChange} />;
  }
}

export default withRouterHooks(PageBase);
