import React from 'react';
import getContactUsErrorList from 'data/ContactUs-ErrorList';
import TextInputLcc from 'views/components/inputs/Text';
import MultiCheckboxInputLcc from 'views/components/inputs/MultiCheckbox';
import RadioInputLcc from 'views/components/inputs/Radio';
import TextareaInputLcc from 'views/components/inputs/Textarea';
import DateInputLcc from 'views/components/inputs/Date';
import TimeInputLcc from 'views/components/inputs/Time';
import FileUploadInputLcc from 'views/components/inputs/FileUpload';
import DropdownInputLcc from 'views/components/inputs/Dropdown';
import CheckboxInputLcc from 'views/components/inputs/Checkbox';
import ErrorSummaryLcc from 'views/components/ErrorSummaryLcc';

export default class DynamicPage extends React.Component {
    constructor(props) {
        super(props);
        this.state = { errors: [] };
        this.handleChange = this.handleChange.bind(this);
    }

    handleChange(e) {
        //lifting up
        this.props.onValueChange(e);
    }

    validateForm = () => {
        let errors = []
        let formIsValid = true

        //Call a lambda instead here where pass form name and page and values
        //Will only bring for that page really
        let contactUsErrorList = getContactUsErrorList();
        formIsValid = contactUsErrorList.errorList.length > 0 ? false : true;
        errors = contactUsErrorList.errorList;
        this.setState({ errors })

        return formIsValid
    }
    //This won't be here, just for dev purposes - will be on submit
    onCheckErrors = event => {
        event.preventDefault();
        if (this.validateForm()) {
            console.log("valid", this.state.errors);
        }
        else {
            console.log("invalid", this.state.errors);
        }

    };
    render() {
        let pageData = this.props.pageData;
        let showHeadingOnComponent = pageData.questions.length > 1 || pageData.questions[0].componentType === "Checkbox" ? false : true;

        const components = {
            Checkbox: CheckboxInputLcc,
            Text: TextInputLcc,
            MultiCheckbox: MultiCheckboxInputLcc,
            Radio: Radio,
            Textarea: Textarea,
            Date: Date,
            Time: Time,
            FileUpload: FileUpload,
            Dropdown: Dropdown
        };

        return (
            <>
                {!showHeadingOnComponent
                    ? <h1 className="heading-large">{pageData.displayName}</h1>
                    : null}
                {(pageData.errorSummaryTitle && this.state.errors.length > 0)
                    ? <ErrorSummaryLcc errorSummaryTitle={pageData.errorSummaryTitle} errorSummaryHint={pageData.errorSummaryHint} errors={this.state.errors} />
                    : null}
                {pageData.questions.map((question) => {
                    const ComponentType = components[question.componentType];
                    let componentErrors = [];
                    let thisQuestionsErrors = this.state.errors.filter(e => e.id === question.id);
                    if (thisQuestionsErrors.length > 0) {
                        componentErrors = thisQuestionsErrors[0].errors;
                    }
                    return (
                        <div key={question.id} className={`form-group ${componentErrors.length > 0 ? "form-group-error" : ""}`}>
                            <ComponentType qnData={question} showHeadingOnComponent={showHeadingOnComponent} errors={componentErrors}
                                onValueChange={this.handleChange} //{...values}
                            />
                        </div>
                    );
                })
                }
                <div>
                    <button className="btn btn-primary btn-small" id="TestErrors" onClick={this.onCheckErrors}> Check errors (for now) </button>
                </div>
            </>
        );
    }
}
