import React from 'react';
import ErrorMessageLcc from 'views/components/display/ErrorMessage';
import HintLcc from 'views/components/display/Hint';
import HeaderLarge from '../display/HeaderLarge';

export default class DateInputLcc extends React.Component {
  render() {
    return (
      <>
        <fieldset>
          <QuestionLabel qnData={this.props.qnData} qnCount={this.props.qnCount} useLegend={true} />
          
          {this.props.errors.map((error) => {
            return <ErrorMessageLcc errorMessage={error.errorMessage} />;
          })}
          <div className="form-date">
            <div className="form-group form-group-day">
              <label htmlFor={`${this.props.qnData.name}-day`}>Day</label>
              <input className={`form-control ${this.props.errors.filter(e => e.invalidDay).length > 0 ? "form-control-error" : ""}`} id={`${this.props.qnData.name}-day-id`} name={`${this.props.qnData.name}-day`} type="text" inputMode="numeric" />
            </div>
          </div>
          <div className="form-date">
            <div className="form-group form-group-month">
              <label htmlFor={`${this.props.qnData.name}-month`}>Month</label>
              <input className={`form-control ${this.props.errors.filter(e => e.invalidMonth).length > 0 ? "form-control-error" : ""}`} id={`${this.props.qnData.name}-month-id`} name={`${this.props.qnData.name}-month`} type="text" inputMode="numeric" />
            </div>
          </div>
          <div className="form-date">
            <div className="form-group form-group-year">
              <label htmlFor={`${this.props.qnData.name}-year`}>Year</label>
              <input className={`form-control ${this.props.errors.filter(e => e.invalidYear).length > 0 ? "form-control-error" : ""}`} id={`${this.props.qnData.name}-year-id`} name={`${this.props.qnData.name}-year`} type="text" inputMode="numeric" />
            </div>
          </div>
        </fieldset>
      </>
    );
  }
}
