import React from 'react';
import getContactUsServiceRequest from 'data/ContactUs-ServiceRequest';
import GenericPrivacyNotice from 'views/components/GenericPrivacyNotice';
import SummaryCategory from 'views/components/SummaryCategory';

export default class Summary extends React.Component {
    
    render() {
        let contactUsServiceRequest = getContactUsServiceRequest();
        return (
          <>

            <h1 className="heading-large">
                <span className="form-progress">
                    {contactUsServiceRequest.displayName}
                </span>Check your answers before submitting your form
            </h1>

            <div className="text">
                <p><strong>{contactUsServiceRequest.formSummaryCustomTextHeader }</strong></p>

                {contactUsServiceRequest.formIncludesPayment
                    ? <p>If your details are correct, select 'Continue to payment' to continue to the payment section. If you need to change anything, select the 'Back' link to go back through the form.</p>
                    : <p>If your details are correct, select 'Submit' to send the form. If you need to change anything, select the 'Back' link to go back through the form.</p>
                }

            </div>

            {contactUsServiceRequest.summaryCategories.map((category) => {
               return (
                   <SummaryCategory catProps={category} />
               );
            })}

            <div className="text">
                <div className="notice">
                    <i className="fa fa-exclamation-triangle" aria-hidden="true">
                        <span className="sr-only">Warning</span>
                    </i>
                    <strong className="bold-small">Please note that by submitting this form you agree to give consent for Leeds City Council to use your personal information.</strong>
                </div>
            </div>

            <GenericPrivacyNotice/>

            <p><strong>{contactUsServiceRequest.SummaryCustomTextFooter}</strong></p>

        </>
    );
  }
}
