import React from 'react';
import { Outlet } from 'react-router-dom';
import withRouterHooks from "helpers/withRouterHooks";

class FormBase extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      form: this.props.params.form
    };

    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleSubmit(event) {
    event.preventDefault();
    //Call validation lambda - pass values from state
    //If valid call lambda - pass form and page to find next page, rather than this:
    let nextPage;
    // switch (this.props.params.page) {
    //   case 'Description':
    //     nextPage = "Name";
    //     break;
    //   case 'Name':
    //     nextPage = "Favourites";
    //     break;
    //   case 'Favourites':
    //     nextPage = "WhichPage";
    //     break;
    //   case 'WhichPage':
    //     nextPage = "Problem";  //Will depend on value chosen really
    //     break;
    //   case 'Problem':
    //     nextPage = "Declaration";
    //     break;
    //   default:
    //     nextPage = "Summary";
    // }
    // this.setState({ page: nextPage });
    // // this.props.navigate("/" + this.state.form + "/" + nextPage);

    /*  */
    let currentPageIndex = this.state.json.pages.findIndex(page => page.name === this.props.params.page);
    nextPage = this.state.json.pages[currentPageIndex + 1];

    if (nextPage != null) {
      this.setState({ page: nextPage });
      this.props.navigate("/" + this.state.form + "/" + nextPage.name);
    } else {
      alert("This is the last page :(");
    }

  }

  render() {
    //const onValueChange = 'Thing to pass'
    return (

      <>
        <br />
        <div className='form-group'>
          <button className='btn btn-info'>Back</button>
        </div>

        <form onSubmit={this.handleSubmit}>
          <Outlet />
          {/*<Outlet context={[onValueChange]} />*/}

          <div className='form-group'>
            <button className='btn btn-success' type='submit'>Continue</button>
          </div>
        </form>
      </>
    );
  }
}

export default withRouterHooks(FormBase);
