import React from 'react';
import ErrorMessageLcc from 'views/components/ErrorMessageLcc';
import HintLcc from 'views/components/HintLcc';

export default class CheckboxInputLcc extends React.Component {
    render() {
        return (
            <>            
                <HintLcc hint={this.props.qnData.hint} />
                {this.props.errors.map((error) => {
                    return <ErrorMessageLcc errorMessage={error.errorMessage} />
                })
                }   
                <label className="block-label" htmlFor={this.props.qnData.name}>
                    <input type={this.props.qnData.inputType} id={this.props.qnData.id} name={this.props.qnData.name} value={this.props.qnData.value} required={this.props.qnData.isRequired} />
                    <span className="form-label-bold">{this.props.qnData.displayName}</span>
                </label>
            </>
        )
    }
}
