import React from 'react';
import ErrorMessageLcc from 'views/components/display/ErrorMessage';
import QuestionLabelLcc from '../display/QuestionLabel';

export default class TextareaInputLcc extends React.Component {
    constructor(props) {
        super(props);
        this.state = { charCount: 0 };
        this.onChangeValue = this.onChangeValue.bind(this);
    }

    onChangeValue(event) {
        this.setState({
            charCount: event.target.value.length
        });
    }

  render() {
    return (
      <>
        <QuestionLabelLcc qnData={this.props.qnData} showQuestionLabel={this.props.showQuestionLabel} />

        <ErrorMessageLcc errors={this.props.errors} />

        <textarea className={`form-control ${this.props.errors.length > 0 ? "form-control-error" : ""}`} id={this.props.qnData.name} name={this.props.qnData.name} rows={this.props.qnData.rows} cols={this.props.qnData.cols} required={this.props.qnData.isRequired}
                    onChange={this.onChangeValue}>
        </textarea>
        <p>You have {1000 - this.state.charCount} characters remaining</p>
      </>
    );
  }
}
