import React from 'react';
import LccLinkTile from './LccLinkTile';
import { useParams, useNavigate, Outlet } from 'react-router-dom';
import getCategoryList from '../Data/Categories';

export default function FormList() {
  return (
    <FormListComponent params={useParams()} navigate={useNavigate()} />
  );
}

class FormListComponent extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      category: this.props.params.category,
    };
  }

  render() {
    //let formList = getFormList(this.state.category);
    let categoryList = getCategoryList();
    let category = categoryList.categories.find(element => element.name == this.state.category)

    return (
      <>
        <div class="text">
          <h1>{categoryList.friendlyName}</h1>
        </div>

        <div class="groupPromo">
          <div class="listWebpart">
            <ul>
              {
                category.forms.map((form) => {
                  let to = form.name + "/Description";
                  return (
                    <li>
                      <LccLinkTile to={to} header={form.friendlyName} description={form.description} />
                    </li>
                  );
                })
              }

            </ul>
          </div>
        </div>
      </>
    );
  }
}

function getFormList(category) {
  //TODO API call to get forms

  switch (category) {
    case "ContactUs":
      return {
        header: "Contact us",
        forms: [
          { to: "/ContactUsPathways/Description", formName: "Contact Us Pathways", description: "" }
        ]
      };

    case "Search":
      return [
        //{ to: "/ContactUsPathways/Description", header: "Contact Us Pathways", description: "" }
      ];

    default:
      return null;
  }
}
