import React from 'react';
import HeaderLarge from './HeaderLarge';
import HintLcc from './Hint';
import Label from './Label';

export default class QuestionLabel extends React.Component {
  render() {
    let LabelElement = this.props.qnCount > 1
      ? <Label name={this.props.qnData.name} displayName={this.props.qnData.displayName} />
      : <HeaderLarge name={this.props.qnData.name} displayName={this.props.qnData.displayName} />;

    return (
      <>
        {this.props.useLegend
          ? <legend>{LabelElement}</legend>
          : LabelElement
        }

        <HintLcc hint={this.props.qnData.hint} />
      </>
    );
  }
}
