import React from 'react';

export default () => {
  const onDragStart = (event, nodeType) => {
    event.dataTransfer.setData('application/reactflow', nodeType);
    event.dataTransfer.effectAllowed = 'move';
  };

  return (
    <aside style={{"backgroundColor": "lightgrey"}}>
      <div className="description">You can drag these into a page.</div>
      <div className="dndnode" onDragStart={(event) => onDragStart(event, 'Textbox')} draggable>
        Textbox
      </div>
      <div className="dndnode" onDragStart={(event) => onDragStart(event, 'Date')} draggable>
        Date
      </div>
      <div className="dndnode output" onDragStart={(event) => onDragStart(event, 'output')} draggable>
        Output Node
      </div>
    </aside>
  );
};
