import React, { Fragment } from 'react';
import withRouterHooks from "helpers/withRouterHooks";
import { Navigate } from "react-router-dom";
import Description from 'views/shared/Description';

class PageBase extends React.Component {

  render() {
    let formJson = require("../../forms/" + this.props.params.form + "/Config.json");
    let category = formJson.categories.find(category => category.name == this.props.params.page);

    if (category === undefined) {
      return <Navigate to="/ErrorNotFound" />;
    }

    return (
      /**
       * Change questions to sections?
       * Non question sections:
       * - text/body/paragraph (note an input can also be Text)
       * - headings/subheadings?
       *
       * Idea could be to store all text strings in the config as well,
       * but would then need to be able to loop through those just like
       * questions and not get too complex
       * ---
       * Instead of that, could have a flat property/array that puts strings
       * at the top right under the header but nowhere else
       */

      /**
       * 3rd level?
       * Categories
       *  Pages
       *   Questions/Text/Sections
       */
      <>
        <h1>{this.props.header}</h1>


        {
          category.questions.map((question) => {
            switch (question.type) {
              case "Description":
                return (
                  <Description header={question.header} body={question.body} />
                );

              case "Paragraph":
                return (
                  <>
                    <h2>{question.friendlyName}</h2>
                    <p>{question.value}</p>
                  </>
                );

              case "Text":
                return (
                  <Fragment key={question.name}>
                    <label className='form-label'>{question.friendlyName}</label>
                    <input className='form-control'></input>
                  </Fragment>
                );

              default:
                break;
            }
          })
        }
      </>

    );
  }

}

export default withRouterHooks(PageBase);
