import React from 'react';
import ErrorMessageLcc from 'views/components/display/ErrorMessage';
import QuestionLabelLcc from '../display/QuestionLabel';
import HintLcc from 'views/components/HintLcc';

export default class TextInputLcc extends React.Component {
  render() {
    return (
      <>
        <QuestionLabelLcc qnData={this.props.qnData} qnCount={this.props.qnCount} />

        <ErrorMessageLcc errors={this.props.errors} />

        <input className={`form-control ${this.props.errors.length > 0 ? "form-control-error" : ""}`} type={this.props.qnData.inputType} id={this.props.qnData.name} name={this.props.qnData.name} required={this.props.qnData.isRequired} />
      </>
    );
  }
}


class TextInputLcc2 extends React.Component {
  constructor(props) {
    super(props);
    this.handleChange = this.handleChange.bind(this);
  }
  handleChange(e) {
    //lifting up
    this.props.onValueChange(e);
  }
  render() {
    return (
      <>
        <QuestionLabelLcc qnData={this.props.qnData} qnCount={this.props.qnCount} />

        <ErrorMessageLcc errors={this.props.errors} />

        <input className={`form-control ${this.props.errors.length > 0 ? "form-control-error" : ""}`} type={this.props.qnData.inputType} id={this.props.qnData.id} name={this.props.qnData.name} required={this.props.qnData.isRequired}
          onChange={this.handleChange}
        />
      </>
    );
  }
}
