import React from 'react'

import TextInputLcc from 'views/components/inputs/Text';
import MultiCheckboxInputLcc from 'views/components/inputs/MultiCheckbox';
import RadioInputLcc from 'views/components/inputs/Radio';
import TextareaInputLcc from 'views/components/inputs/Textarea';
import DateInputLcc from 'views/components/inputs/Date';
import TimeInputLcc from 'views/components/inputs/Time';
import FileUploadInputLcc from 'views/components/inputs/FileUpload';
import DropdownInputLcc from 'views/components/inputs/Dropdown';
import CheckboxInputLcc from 'views/components/inputs/Checkbox';
import ParagraphLcc from 'views/components/inputs/Paragraph';

export default class InputComponentWrapper extends React.Component {
  render() {

    const components = {
      Checkbox: CheckboxInputLcc,
      Text: TextInputLcc,
      MultiCheckbox: MultiCheckboxInputLcc,
      Radio: RadioInputLcc,
      Textarea: TextareaInputLcc,
      Date: DateInputLcc,
      Time: TimeInputLcc,
      FileUpload: FileUploadInputLcc,
      Dropdown: DropdownInputLcc,
      Paragraph: ParagraphLcc
    };

    let qnData = this.props.qnData;
    const ComponentType = components[qnData.componentType];

    return (
      <ComponentType name={qnData.name} name={qnData.name} showQuestionLabel={this.props.showQuestionLabel} errors={this.props.errors} onValueChange={this.props.handleChange} />
    );
  }
}
