import React, { useState, useRef, useCallback } from 'react';
import ReactFlow, {
  ReactFlowProvider,
  addEdge,
  useNodesState,
  useEdgesState,
  Controls,
  MiniMap
} from 'react-flow-renderer';
import Sidebar from './Sidebar';
import './index.css';
import './button.css';

import ButtonEdge from './ButtonEdge.js';
import getNewId from 'helpers/getNewId';

const initialNodes = [
  {
    id: 'IntroPage',
    //type: 'group',
    type: 'input',
    data: { label: 'Introduction page' },
    position: { x: 0, y: 0 },
    style: { width: 170, height: 140 },
    connectable: false,
    //draggable: false,
    sourcePosition: 'right',
    targetPosition: 'left'
  },
  {
    id: 'Textbox1',
    type: 'input',
    //type: 'group',
    data: { label: 'My textbox' },
    position: { x: 10, y: 50 },
    draggable: false,
    parentNode: 'IntroPage',
    //expandParent: true,
    extent: 'parent'
  },
  {
    id: 'SummaryPage',
    //type: 'group',
    type: 'output',
    data: { label: 'Summary page' },
    position: { x: 250, y: 0 },
    style: { width: 170, height: 140 },
    connectable: false,
    //draggable: false,
    sourcePosition: 'right',
    targetPosition: 'left'
  },
];

const initialEdges = [
  { id: 'e1-2', source: 'IntroPage', target: 'SummaryPage', type: 'buttonedge' },
];

const edgeTypes = {
  buttonedge: ButtonEdge,
};

const getId = () => getNewId();

function App() {
  const reactFlowWrapper = useRef(null);
  const [nodes, setNodes, onNodesChange] = useNodesState(initialNodes);
  const [edges, setEdges, onEdgesChange] = useEdgesState(initialEdges);
  const [reactFlowInstance, setReactFlowInstance] = useState(null);

  const onConnect = useCallback((params) => setEdges((eds) => addEdge(params, eds)), []);

  const onDragOver = useCallback((event) => {
    event.preventDefault();
    event.dataTransfer.dropEffect = 'move';
  }, []);

  const onDrop = useCallback((event) => {
    event.preventDefault();

    const reactFlowBounds = reactFlowWrapper.current.getBoundingClientRect();
    const type = event.dataTransfer.getData('application/reactflow');

    // check if the dropped element is valid
    if (typeof type === 'undefined' || !type) {
      return;
    }

    const position = reactFlowInstance.project({
      x: event.clientX - reactFlowBounds.left,
      y: event.clientY - reactFlowBounds.top,
    });

    const newNode = {
      id: getId(),
      type,
      position,
      data: { label: `${type} node` },
    };

    setNodes((nds) => nds.concat(newNode));
  },
    [reactFlowInstance]
  );

  let nodeId = 0;
  const addNode = useCallback(() => {
    const id = `${++nodeId}`;
    const newNode = {
      id,
      position: {
        x: Math.random() * 50,
        y: Math.random() * 50,
      },
      data: {
        label: `Node ${id}`,
      },
    };
    setNodes((nds) => nds.concat(newNode));
  }, []);

  return (
    <div className="dndflow">
      <ReactFlowProvider>
        <div className="reactflow-wrapper" ref={reactFlowWrapper}>
          <ReactFlow
            nodes={nodes}
            edges={edges}
            onNodesChange={onNodesChange}
            onEdgesChange={onEdgesChange}
            onConnect={onConnect}
            onInit={setReactFlowInstance}
            onDrop={onDrop}
            onDragOver={onDragOver}
            edgeTypes={edgeTypes}
            fitView
          >
            <button onClick={addNode} className="btn-add">
              Add node
            </button>

            <Controls />
            <MiniMap />
          </ReactFlow>
        </div>
        <Sidebar />
      </ReactFlowProvider>
    </div>
  );
};

export default App;
