import React from 'react';
import ErrorMessageLcc from 'views/components/display/ErrorMessage';
import QuestionLabelLcc from '../display/QuestionLabel';

export default class TextareaInputLcc extends React.Component {
  render() {
    return (
      <>
        <QuestionLabelLcc name={this.props.name} displayName={this.props.displayName} hint={this.props.hint} showQuestionLabel={this.props.showQuestionLabel} />

        <ErrorMessageLcc errors={this.props.errors} />

        <textarea className={`form-control ${this.props.errors.length > 0 ? "form-control-error" : ""}`} id={this.props.name} name={this.props.name} rows={this.props.rows} cols={this.props.cols} required={this.props.isRequired} ></textarea>
      </>
    );
  }
}
