import React from 'react';

export default class HintLcc extends React.Component {
  render() {
    return (
      <>
        {
          this.props.hint ?
            <span className="form-hint text" > {this.props.hint}</span>
            : null
        }


        {
          this.props.hints.map((hint) => {
            <span className="form-hint text" > {hint}</span>
          })
        }
      </>
    );
  }
}
