import React from 'react';

export default class HintLcc extends React.Component {
  render() {
    return (
      <>
        {
          this.props.hint ?
            <span className="form-hint text" > {this.props.hint}</span>
            : null
        }


        {
          this.props.hints.questions.map((question) => {
            const ComponentType = components[question.componentType];
            let componentErrors = [];
            let thisQuestionsErrors = this.state.errors.filter(e => e.id === getQuestionId(question.name));
            if (thisQuestionsErrors.length > 0) {
              componentErrors = thisQuestionsErrors[0].errors;
            }

            return (
              <div key={question.name + "FormGroup"} className={`form-group ${componentErrors.length > 0 ? "form-group-error" : ""}`}>
                <ComponentType key={getQuestionId(question.name)} qnData={question} qnCount={qnCount} errors={componentErrors} />
              </div>
            );
          })
        }
      </>
    );
  }
}
