import React from 'react';
import ErrorMessageLcc from 'views/components/display/ErrorMessage';
import QuestionLabelLcc from '../display/QuestionLabel';

export default class DateInputLcc extends React.Component {
  render() {
    return (
      <>
        <fieldset>
          <QuestionLabelLcc name={this.props.name} displayName={this.props.displayName} hint={this.props.hint} showQuestionLabel={this.props.showQuestionLabel} useLegend={true} />

          <ErrorMessageLcc errors={this.props.errors} />

          <div className="form-date">
            <div className="form-group form-group-day">
              <label htmlFor={`${this.props.name}-day-id`}>Day</label>
              <input className={`form-control ${this.props.errors.filter(e => e.invalidDay).length > 0 ? "form-control-error" : ""}`} id={`${this.props.name}-day-id`} name={`${this.props.qnData.name}-day`} type="text" inputMode="numeric" />
            </div>
          </div>
          <div className="form-date">
            <div className="form-group form-group-month">
              <label htmlFor={`${this.props.name}-month-id`}>Month</label>
              <input className={`form-control ${this.props.errors.filter(e => e.invalidMonth).length > 0 ? "form-control-error" : ""}`} id={`${this.props.name}-month-id`} name={`${this.props.qnData.name}-month`} type="text" inputMode="numeric" />
            </div>
          </div>
          <div className="form-date">
            <div className="form-group form-group-year">
              <label htmlFor={`${this.props.name}-year-id`}>Year</label>
              <input className={`form-control ${this.props.errors.filter(e => e.invalidYear).length > 0 ? "form-control-error" : ""}`} id={`${this.props.name}-year-id`} name={`${this.props.qnData.name}-year`} type="text" inputMode="numeric" />
            </div>
          </div>
        </fieldset>
      </>
    );
  }
}
