import React from 'react';
import CheckboxInputLcc from 'views/components/inputs/Checkbox';
import ErrorMessageLcc from 'views/components/display/ErrorMessage';
import HintLcc from 'views/components/display/Hint';
import HeaderLarge from '../display/HeaderLarge';
import QuestionLabel from '../display/Label';

export default class MultiCheckboxInputLcc extends React.Component {
  render() {
    return (
      <>
        <fieldset>
          <legend>
            {this.props.qnCount > 1
              ? <QuestionLabel name={this.props.qnData.name} displayName={this.props.qnData.displayName} hint={this.props.qnData.hint} />
              : <HeaderLarge displayName={<QuestionLabel name={this.props.qnData.name} displayName={this.props.qnData.displayName} />} hint={this.props.qnData.hint}/>
            }
          </legend>
          <HintLcc hint={this.props.qnData.hint} />
          {this.props.errors.map((error) => {
            return <ErrorMessageLcc errorMessage={error.errorMessage} />;
          })
          }
          {this.props.qnData.values.map((value) => {
            return (
              <CheckboxInputLcc key={value.id} qnData={value} errors={[]} />
            );
          })
          }
        </fieldset>
      </>
    );
  }
}
