import React from 'react';
import HeaderLarge from 'views/components/display/HeaderLarge';
import TextInputLcc from 'views/components/inputs/Text';
import DropdownInputLcc from 'views/components/inputs/Dropdown';
import getAddressList from 'data/Addresses';

export default class Address extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            addresses: {"options": []},
            showDropdown: false,
            errors: []
        };
        this.handleChange = this.handleChange.bind(this);
        this.findAddress = this.findAddress.bind(this);
    }

    //This will probably move
    handleChange(e) {
        console.log("ignore for now");
    }

    findAddress(e) {
        e.preventDefault();
        let addressList = getAddressList();
        //If no results or api down, show error
        if (addressList.options.length > 0) {
            this.setState({
                addresses: addressList,
                showDropdown: true
            });
        }
        else {
            let errors = [{ "errorName": "InvalidPostcode", "errorMessage": "Enter a valid postcode/street name within the Leeds city council area"}]
            this.setState({
                showDropdown: false,
                errors
            });           
        }
    }

    render() {
        let postcodeEntryQuestion = this.props.formJson.questions.find(q => q.name === "PostcodeEntry");
    return (
        <>
            <HeaderLarge displayName={this.props.formJson.displayName} />
            <div key={"PostcodeEntryFormGroup"} className={`form-group ${this.state.errors > 0 ? "form-group-error" : ""}`}>
                <TextInputLcc key={postcodeEntryQuestion.id} qnData={postcodeEntryQuestion} showQuestionLabel={true} errors={this.state.errors} onValueChange={this.handleChange} />
            </div>
            <div className='form-group'>
                <button className='btn btn-primary' onClick={ this.findAddress } >Find address</button>
            </div>
            <div id="address-div" className={`collapse ${this.state.showDropdown ? "show" : ""}`}>
                <div key={"AddressPickerFormGroup"} className="form-group">
                    <DropdownInputLcc key={this.state.addresses.id} qnData={this.state.addresses} showQuestionLabel={true} errors={[]} onValueChange={this.handleChange} />
                </div>
            </div>
            
      </>
    );
  }
}
