import React from 'react';
import LccLinkTile from './LccLinkTile';
import getCategoryList from '../Data/Categories';

export default class CategoryList extends React.Component {

  render() {
    let categories = getCategoryList();

    return (
      <>
        <div class="text">
          <h1>Online services</h1>
        </div>

        <div class="groupPromo">
          <div class="listWebpart">
            <ul>
              {
                categories.categories.map((element) => {
                  return (
                    <li key={element.name}>
                      <LccLinkTile to={element.to} header={element.header} description={element.description} />
                    </li>
                  );
                })
              }

            </ul>
          </div>
        </div>
      </>
    );
  }
}

function getCategoryListLocal() {
  //TODO API call to get categories
  return [
    { to: "/Categories/ContactUs", header: "Contact us", description: "" }
  ];
}
