import React from 'react';
import ErrorMessageLcc from 'views/components/ErrorMessageLcc';
import HintLcc from 'views/components/HintLcc';

export default class TextInputLcc extends React.Component {
  render() {
    return (
      <>
        {this.props.qnCount > 1
          ? <label className="form-label-bold" htmlFor={this.props.qnData.name}>{this.props.qnData.displayName}<HintLcc hint={this.props.qnData.hint} /></label>
          : <h1 className="heading-large"><label className="form-label-bold" htmlFor={this.props.qnData.name}>{this.props.qnData.displayName}<HintLcc hint={this.props.qnData.hint} /></label></h1>
        }

        {this.props.errors.map((error) => {
          return <ErrorMessageLcc errorMessage={error.errorMessage} />;
        })
        }
        <input className={`form-control ${this.props.errors.length > 0 ? "form-control-error" : ""}`} type={this.props.qnData.inputType} id={this.props.qnData.id} name={this.props.qnData.name} required={this.props.qnData.isRequired} />
      </>
    );
  }
}
