import React from 'react';
import { Outlet } from 'react-router-dom';
import withHooks from "helpers/withHooks";
import {Navigate} from 'react-router-dom';

class FormBase extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      form: "SecondForm" //This can be pulled from params, but is hard coded here for demo purposes
    };

    this.handleSubmit = this.handleSubmit.bind(this);
  }

  render() {
    return (
      <>
        <br />
        <div className='form-group'>
          <button className='btn btn-info'>Back</button>
        </div>

        <form onSubmit={this.handleSubmit}>
          <Outlet />

          <div className='form-group'>
            <button className='btn btn-success' type='submit'>Continue</button>
          </div>
        </form>
      </>
    );
  }

  /// Mock submit function to demo .bind() and .navigate()
  handleSubmit(e) {
    e.preventDefault(); //Stops the form automatically submitting the page in a way we don't control
    let nextPage = "Description"; //Lambda call goes here, hard string used for demo
    this.setState({ page: nextPage });
    this.props.navigate("/" + this.state.form + "/" + nextPage);
    return <Navigate to="/ErrorNotFound" />;
  };
}

export default withHooks(FormBase);
