import React from 'react';
import '../../../App.css';
import ErrorMessageLcc from 'views/components/display/ErrorMessage';
import HintLcc from 'views/components/display/Hint';
import QuestionLabel from '../display/QuestionLabel';

export default class FileUploadInputLcc extends React.Component {

    state = { selectedFile: null };

    onFileChange = event => {
        console.log("event.target.files", event.target.files);
        this.setState({ selectedFile: event.target.files[0] });
    };

    onFileUpload = (event) => {
        event.preventDefault();
        //TODO Add to list with Remove button



        //formData = new FormData();
        //formData.append(
        //    this.state.selectedFile.name,
        //    this.state.selectedFile
        //);

        // Details of the uploaded file
        //console.log("Do something with this", this.state.selectedFile);
        //for (var key of formData.entries()) {
        //    console.log(key[0] + ", " + key[1]);
        //}
    };

    fileData = () => {
        //TO DO Displays when click upload, not when select
        if (this.state.selectedFile) {

            return (
                <div className="well">
                    <table className="table">
                            <thead>
                                <tr>
                                    <th>File name</th>
                                    <th>Description</th>
                                    <th></th>
                                </tr>
                            </thead>
                            <tbody>
                            <tr>
                                <td className="break--word">{this.state.selectedFile.name}</td>
                                    <td>Proof of bank details</td>
                                    {/*<td>*/}
                                    {/*<form action="/FileUpload/RemoveUploadedFile?index=0" data-ajax="true" data-ajax-begin="DisplayLoadingSpinner(this)" data-ajax-failure="RedirectToTechnicalProblemPage();" data-ajax-method="POST" data-ajax-mode="replace" data-ajax-success="DisplayMasterContent();" data-ajax-update="#FileUploadResultsContainer" id="DeleteAttachmentForm" method="post">*/}
                                    {/*    <input name="__RequestVerificationToken" type="hidden" value="yXfeXeVXlDPZyu4lC9fDa20NuNPinZm1xJJd4TBI5DE5SvLAOeCOSmyb8laFciLDdOpQz7UgEVPz6JPBcbpRl7cBeBk1"/>*/}
                                    {/*    <input id="ControllerToRedirectAfter" name="ControllerToRedirectAfter" type="hidden" value="BusinessGrants"/>*/}
                                    {/*    <input id="ActionToRedirectAfter" name="ActionToRedirectAfter" type="hidden" value="Page_18"/>*/}
                                    {/*    <input id="ReferenceNumber" name="ReferenceNumber" type="hidden" value="" />*/}
                                    {/*    <input type="submit" value="Remove file" class="btn btn-secondary btn-small bottom"/>*/}
                                    {/*</form>*/}
                                    {/*</td>*/}
                                </tr>
                            </tbody>
                        </table>
                </div>

            );
        } else {
            return <p>No files/attachments have been added to the list.</p>
            ;
        }
    };

    render() {
        return (
            <>
            <QuestionLabel qnData={this.props.qnData} qnCount={this.props.qnCount} />
                {this.props.errors.map((error) => {
                    return <ErrorMessageLcc errorMessage={error.errorMessage} />
                })
                }
                <div>
                    {/*<label className="btn btn-primary btn-small" htmlFor={this.props.qnData.name}>*/}
                    {/*    Select file*/}
                    <input type={this.props.qnData.inputType} id={this.props.qnData.id} name={this.props.qnData.name}
                        onChange={this.onFileChange}
                        />
                    {/*</label>*/}
                </div>
                {this.state.selectedFile ?
                    <div>
                        <button className="btn btn-primary btn-small" id="UploadAttachmentButton" onClick={this.onFileUpload}>Upload selected file </button>
                    </div>
                    : null}
                <div id="FileUploadResultsContainer" style={{ display: "block" }}>
                    <label className="form-label-bold">List of uploaded files</label>
                    {this.fileData()}
                </div>
            </>
        )
    }
}
