import React from 'react';
import ErrorMessageLcc from 'views/components/ErrorMessageLcc';
import HintLcc from 'views/components/HintLcc';

export default class DropdownInputLcc extends React.Component {
    render() {
        return (
            <>
                {this.props.qnCount > 1
                    ? <label className="form-label-bold" htmlFor={this.props.qnData.name}>{this.props.qnData.displayName}<HintLcc hints={this.props.qnData.hint} /></label>
                    : <h1 className="heading-large"><label className="form-label-bold" htmlFor={this.props.qnData.name}>{this.props.qnData.displayName}<HintLcc hints={this.props.qnData.hint} /></label></h1>
                }

                {this.props.errors.map((error) => {
                    return <ErrorMessageLcc errorMessage={error.errorMessage} />;
                })
                }
                <select className={`form-control ${this.props.errors.length > 0 ? "form-control-error" : ""}`} id={this.props.qnData.id} name={this.props.qnData.name}>
                    <option>{this.props.qnData.defaultOption}</option>
                    {this.props.qnData.options.map((option) => {
                        return (
                            <option key={option.value} value={option.value} >{option.displayValue}</option>
                        );
                    })
                    }
                </select>
            </>
        );
    }
}
