import React from 'react';
import ErrorMessageLcc from 'views/components/display/ErrorMessage';
import QuestionLabelLcc from '../display/QuestionLabel';

export default class TextInputLcc extends React.Component {
  constructor(props) {
    super(props);
    this.handleChange = this.handleChange.bind(this);
  }
  handleChange(e) {
    //lifting up
    this.props.onValueChange(e);
  }
  render() {
    return (
      <>
        <QuestionLabelLcc name={this.props.name} displayName={this.props.displayName} hint={this.props.hint} showQuestionLabel={this.props.showQuestionLabel} />

        <ErrorMessageLcc errors={this.props.errors} />

        <input className={`form-control ${this.props.errors.length > 0 ? "form-control-error" : ""}`} type={this.props.inputType} id={this.props.name} name={this.props.name} required={this.props.isRequired} onChange={this.handleChange} />
      </>
    );
  }
}
