import getInitialNodes from 'helpers/getInitialNodes';
import getNewId from 'helpers/getNewId';
import React, { useCallback, useState } from 'react';
import { getBezierPath, getEdgeCenter, getMarkerEnd, Handle, useNodesState, useReactFlow } from 'react-flow-renderer';

import 'ind';

const foreignObjectSize = 40;

export default function EdgeButton({
  id,
  sourceX,
  sourceY,
  targetX,
  targetY,
  sourcePosition,
  targetPosition,
  style = {},
  markerEnd,data, isConnectable
}) {
  const edgePath = getBezierPath({
    sourceX,
    sourceY,
    sourcePosition,
    targetX,
    targetY,
    targetPosition,
  });

  const [edgeCenterX, edgeCenterY] = getEdgeCenter({
    sourceX,
    sourceY,
    targetX,
    targetY,
  });

  const reactFlowInstance = useReactFlow();

  const onEdgeClick = useCallback((evt) => {
    //evt.preventDefault();
    evt.stopPropagation();

    const newNode = {
      id: getNewId(),
      position: { x: Math.random() * 50, y: Math.random() * 50 },
      data: { label: `Node ${id}`, },
    };
    reactFlowInstance.addNodes(newNode);
  },
    []
  );

  return (
    <>
      <Handle
        type="target"
        position="left"
        style={{ background: '#555' }}
        onConnect={(params) => console.log('handle onConnect', params)}
        isConnectable={isConnectable}
      />
      <div>
        Page title
      </div>
      <Handle
        type="source"
        position="right"
        id="a"
        style={{ top: 10, background: '#555' }}
        isConnectable={isConnectable}
      />
      <Handle
        type="source"
        position="right"
        id="b"
        style={{ bottom: 10, top: 'auto', background: '#555' }}
        isConnectable={isConnectable}
      />
    </>
  );
}
