import React from 'react';
import getContactUsNameFormView from 'data/ContactUs-Name-FormView';
import getContactUsNameErrorList from 'data/ContactUs-Name-ErrorList';
import TextInputLcc from 'views/components/inputs/TextInputLcc';
import MultiCheckboxInputLcc from 'views/components/inputs/MultiCheckboxInputLcc';
import RadioInputLcc from 'views/components/inputs/RadioInputLcc';
import TextareaInputLcc from 'views/components/inputs/TextareaInputLcc';
import DateInputLcc from 'views/components/inputs/DateInputLcc';
import TimeInputLcc from 'views/components/inputs/TimeInputLcc';
import FileUploadInputLcc from 'views/components/inputs/FileUploadInputLcc';
import DropdownInputLcc from 'views/components/inputs/DropdownInputLcc';
import CheckboxInputLcc from 'views/components/inputs/CheckboxInputLcc';
import ErrorSummaryLcc from 'views/components/ErrorSummaryLcc';

export default class Name extends React.Component {

  state = {
    errors: []
  };

  validateForm = () => {
    let errors = [];
    let formIsValid = true;

    //Will be a lambda
    let contactUsNameErrorList = getContactUsNameErrorList();

    formIsValid = contactUsNameErrorList.errorList.length > 0 ? false : true;
    errors = contactUsNameErrorList.errorList;

    this.setState({ errors });

    return formIsValid;
  };

  //This won't be here, just for dev purposes - will be on submit
  onCheckErrors = event => {
    //event.preventDefault();
    if (this.validateForm()) {
      console.log("valid", this.state.errors);
    }
    else {
      console.log("invalid", this.state.errors);
    }

  };

  render() {
    let contactUsNameFormView = getContactUsNameFormView(); //Will be call to lambda
    let qnCount = contactUsNameFormView.questions.length;

    const components = {
      CheckboxInputLcc: CheckboxInputLcc,
      TextInputLcc: TextInputLcc,
      MultiCheckboxInputLcc: MultiCheckboxInputLcc,
      RadioInputLcc: RadioInputLcc,
      TextareaInputLcc: TextareaInputLcc,
      DateInputLcc: DateInputLcc,
      TimeInputLcc: TimeInputLcc,
      FileUploadInputLcc: FileUploadInputLcc,
      DropdownInputLcc: DropdownInputLcc
    };

    return (
      <>
        {qnCount > 1 || contactUsNameFormView.questions[0].componentType === "CheckboxInputLcc"
          ? <h1 className="heading-large">{contactUsNameFormView.displayName}</h1>
          : null}
        {(contactUsNameFormView.errorSummaryTitle && this.state.errors.length > 0)
          ? <ErrorSummaryLcc errorSummaryTitle={contactUsNameFormView.errorSummaryTitle} errorSummaryHint={contactUsNameFormView.errorSummaryHint} errors={this.state.errors} />
          : null}
        {contactUsNameFormView.questions.map((question) => {
          const ComponentType = components[question.componentType];
          let componentErrors = [];
          let thisQuestionsErrors = this.state.errors.filter(e => e.id === question.id);
          if (thisQuestionsErrors.length > 0) {
            componentErrors = thisQuestionsErrors[0].errors;
          }
          return (
            <div key={1} className={`form-group ${componentErrors.length > 0 ? "form-group-error" : ""}`}>
              <ComponentType key={question.id} qnData={question} qnCount={qnCount} errors={componentErrors} />
            </div>
          );
        })
        }

        <div>
          <button className="btn btn-primary btn-small" id="TestErrors" onClick={this.onCheckErrors}> Check errors (for now) </button>
        </div>
      </>
    );
  }
}
