import React from 'react';
import { Outlet } from 'react-router-dom';
import withRouterHooks from "helpers/withRouterHooks";

class FormBase extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      form: this.props.params.form,
      json: require("../../forms/" + this.props.params.form + "/Config.json")
    };

    this.handleSubmit = this.handleSubmit.bind(this);
  }

  render() {
    //const onValueChange = 'Thing to pass'
    return (
      <>
        <br />
        <div className='form-group'>
          <a href="https://www.leeds.gov.uk/contact-us" className="link-back">Back</a>
        </div>

        <form onSubmit={this.handleSubmit}>
          <Outlet />
          {/*<Outlet context={[onValueChange]} />*/}

          <div className='form-group'>
            <button className='btn btn-primary' type='submit'>Continue</button>
          </div>
        </form>
      </>
    );
  }

  /// Mock submit function to demo .bind() and .navigate()
  handleSubmit(e) {
    e.preventDefault(); //Stops the form automatically submitting the page in a way we don't control
    //Call validation lambda - pass values from state
    //If valid call lambda - pass form and page to find next page, rather than this:
    //let nextPage;
    // switch (this.props.params.page) {
    //   case 'Description':
    //     nextPage = "Name";
    //     break;
    //   case 'Name':
    //     nextPage = "Favourites";
    //     break;
    //   case 'Favourites':
    //     nextPage = "WhichPage";
    //     break;
    //   case 'WhichPage':
    //     nextPage = "Problem";  //Will depend on value chosen really
    //     break;
    //   case 'Problem':
    //     nextPage = "Declaration";
    //     break;
    //   default:
    //     nextPage = "Summary";
    // }
    // this.setState({ page: nextPage });
    // // this.props.navigate("/" + this.state.form + "/" + nextPage);

    /**
     * Navigates to next page by stepping through JSON in order,
     * and goes to the Summary if there's no pages left
     *
     * Temporary functionality for development/testing
     */
    let currentPageIndex = this.state.json.pages.findIndex(page => page.name === this.props.params.page);
    let nextPage = this.state.json.pages[currentPageIndex + 1];

    ///////////////////////
    let nextPageTest;
    let currentPage = this.state.json.pages.find(page => page.name === this.props.params.page);

    currentPage.navigationRules.forEach(rule => {
      switch (rule.condition) {
        case "contains":
          if (currentPage.questions.find(question => question.name === rule.dependentProperty).value.includes(rule.dependentValue)) {
            nextPageTest = rule.nextPage;
          } else {
            return false;
          }

        default:
          break;
      }
    });

    ///////////////////////
    if (nextPageTest != null) {
      this.setState({ page: nextPageTest });
      this.props.navigate("/" + this.state.form + "/" + nextPageTest.name);
    } else {
      this.props.navigate("/" + this.state.form + "/Summary");
    }
  };
}

export default withRouterHooks(FormBase);
