import getNewId from 'helpers/getNewId';
import React, { useCallback, useState } from 'react';
import { getBezierPath, getEdgeCenter, getMarkerEnd, useNodesState } from 'react-flow-renderer';

import './index.css';

const foreignObjectSize = 40;

// const onEdgeClick = (evt, setNodes) => {
//   evt.stopPropagation();
//     const id = getNewId();
//     const newNode = {
//       id,
//       position: {x: 0, y: 0,},
//       data: {label: `Node ${id}`,},
//     };
//     setNodes((nds) => nds.concat(newNode));
// };

export default function EdgeButton({
  id,
  sourceX,
  sourceY,
  targetX,
  targetY,
  sourcePosition,
  targetPosition,
  style = {},
  markerEnd,
}) {
  const edgePath = getBezierPath({
    sourceX,
    sourceY,
    sourcePosition,
    targetX,
    targetY,
    targetPosition,
  });

  const [edgeCenterX, edgeCenterY] = getEdgeCenter({
    sourceX,
    sourceY,
    targetX,
    targetY,
  });

  function  onEdgeClick (evt) {
    const [nodes, setNodes, onNodesChange] = useNodesState(null);
    evt.stopPropagation();
    const id = getNewId();
    const newNode = {
      id,
      position: { x: 0, y: 0, },
      data: { label: `Node ${id}`, },
    };
    setNodes((nds) => nds.concat(newNode));
  }//, []);

  const onDrop = useCallback((event) => {
    event.preventDefault();

    const reactFlowBounds = reactFlowWrapper.current.getBoundingClientRect();
    const type = event.dataTransfer.getData('application/reactflow');

    // check if the dropped element is valid
    if (typeof type === 'undefined' || !type) {
      return;
    }

    const position = reactFlowInstance.project({
      x: event.clientX - reactFlowBounds.left,
      y: event.clientY - reactFlowBounds.top,
    });

    const newNode = {
      id: getId(),
      type,
      position,
      data: { label: `${type} node` },
    };

    setNodes((nds) => nds.concat(newNode));
  },
    [reactFlowInstance]
  );





  return (
    <>
      <path
        id={id}
        style={style}
        className="react-flow__edge-path"
        d={edgePath}
        markerEnd={markerEnd}
      />
      <foreignObject
        width={foreignObjectSize}
        height={foreignObjectSize}
        x={edgeCenterX - foreignObjectSize / 2}
        y={edgeCenterY - foreignObjectSize / 2}
        className="edgebutton-foreignobject"
      //requiredExtensions="http://www.w3.org/1999/xhtml"
      >
        <div>
          <button className="edgebutton" onClick={(event) => onEdgeClick(event)}>
            +
          </button>
        </div>
      </foreignObject>
    </>
  );
}
