import React from 'react';

export default class ErrorSummaryLcc extends React.Component {
  render() {
    return (
        <div className="error-summary" role="alert" aria-labelledby="error-summary-heading" tabIndex="-1">
        <h2 className="heading-medium error-summary-heading" id="error-summary-heading">
          Error summary
        </h2>
        <p>"Click on a link to go to the problem"</p>

            <ul className="error-summary-list">
          {
            this.props.errors.map((erroredQuestions) => (
                <div key={erroredQuestions.id + "ErrorQuestion"}>
                {erroredQuestions.errors.map((error) => (
                    <li key={error.id + "ErrorSummary"}><a href={`#${erroredQuestions.name}`}>{error.errorMessage}</a></li>
                )
                )}
              </div>
            )
            )
          }

        </ul>
      </div>
    );
  }
}
