import React, { Component } from "react";
import NavTile from 'Views/Components/NavTile';
import getCategoryList from 'Data/Categories';
import { useParams } from "react-router-dom";

function withParams(Component) {
  return props => <Component {...props} params={useParams()} />;
}

class FormList2 extends React.Component {
    componentDidMount() {
        let { id } = this.props.params;
        this.fetchData(id);
    }

    render() {
      let categoryList = getCategoryList();
      let category = categoryList.categories.find(element => element.name === this.state.category);

      <>
        <div className="text">
          <h1>{category.friendlyName}</h1>
        </div>

        <div className="groupPromo">
          <div className="listWebpart">
            <ul>
              {
                category.forms.map((form) => {
                  let route = "/" + form.name + "/Description";
                  return (
                    <li key={form.name}>
                      <NavTile to={route} header={form.friendlyName} description={form.description} />
                    </li>
                  );
                })
              }

            </ul>
          </div>
        </div>
      </>
    }
}

export default withParams(TaskDetail);
