import React from 'react';
import CheckboxInputLcc from 'views/components/inputs/Checkbox';
import QuestionLabelLcc from '../display/QuestionLabel';
import ErrorMessageLcc from 'views/components/display/ErrorMessage';
import TextInputLcc from './Text';
import TextareaInputLcc from './Textarea';

export default class RadioInputLcc extends React.Component {
  constructor() {
    super();
    this.state = {
      selectedOption: ""
    };
    this.onChangeValue = this.onChangeValue.bind(this);
  }
  onChangeValue(event) {
    this.setState({
      selectedOption: event.target.value
    });
  }

  render() {
    const components = {
      TextInputLcc: TextInputLcc,
      TextareaInputLcc: TextareaInputLcc
    };
    return (
      <>
        <fieldset>
          <QuestionLabelLcc qnData={this.props.qnData} showQuestionLabel={this.props.showQuestionLabel} useLegend={true} />

          <ErrorMessageLcc errors={this.props.errors} />

          <div>
            {this.props.qnData.values.map((value) => {
              return (
                <div key={value.id}>
                  <label className="block-label" htmlFor={value.name}>
                    <input type="radio" id={value.id} name={value.name} value={value.value} onChange={this.onChangeValue} />
                    <span className="form-label-bold">{value.displayName}</span>
                  </label>

                  {value.childQuestions.map((question) => {
                    //Will be passed down
                    let componentErrors = [];
                    const ComponentType = components[question.componentType];
                    
                    return (
                      <div key={question.id} className={"panel panel-border-narrow"}
                        style={value.value === this.state.selectedOption ? {} : { display: 'none' }}
                        id="PanelToShow">
                        <div className={`form-group ${componentErrors.length > 0 ? "form-group-error" : ""}`}>
                          <ComponentType key={question.id} qnData={question} showQuestionLabel={false} errors={componentErrors}
                            onValueChange={this.props.onValueChange} //{...values}
                          />
                        </div>
                      </div>

                    );
                  })
                  }
                </div>
              );
            })
            }
          </div>
        </fieldset>
      </>
    );
  }
}
