import React from 'react';
import ErrorMessageLcc from 'views/components/display/ErrorMessage';
import QuestionLabelLcc from '../display/QuestionLabel';

export default class RadioInputLcc extends React.Component {
  constructor() {
    super();
    this.state = {
      selectedOption: ""
    };
    this.onChangeValue = this.onChangeValue.bind(this);
  }
  onChangeValue(event) {
    this.setState({
      selectedOption: event.target.value
    });
  }

  render() {
    return (
      <>
        <fieldset>
          <QuestionLabelLcc qnData={this.props.qnData} showQuestionLabel={this.props.showQuestionLabel} useLegend={true} />

          <ErrorMessageLcc errors={this.props.errors} />

          <div>
            {this.props.qnData.options.map((option) => {
              return (
                <div key={option.name}>
                  <label className="block-label" htmlFor={option.name}>
                    <input type="radio" id={option.name} name={this.props.qnData.name} value={option.value} onChange={this.onChangeValue} />
                    <span className="form-label-bold">{option.displayName}</span>
                  </label>
                </div>
              );
            })
            }
          </div>
        </fieldset>
      </>
    );
  }
}
