import React from 'react';
import { Outlet } from 'react-router-dom';
import useParamsAndNavigate from "Helpers/useParamsAndNavigate";

class FormBase extends React.Component {
  constructor(props) {
    super(props);

    //this.handleSubmit = this.handleSubmit.bind(this);
  }
  render() {
    return (
      <>
        <br />
        <div className='form-group'>
          <button className='btn btn-info' onClick={this.handleBackButton}>Back</button>
        </div>

        <form onSubmit={this.handleSubmit}>
          <Outlet />

          <div className='form-group'>
            <button className='btn btn-success' type='submit'>Continue</button>
          </div>
        </form>
      </>
    );
  }

  handleSubmit(e) {
    e.preventDefault();
    let nextPage = "Test";
    this.setState({ page: nextPage });
    this.props.navigate("/" + this.state.formName + "/" + nextPage);
  };
}

export default useParamsAndNavigate(FormBase);
