import React from 'react';
import LccLinkTile from './LccLinkTile';

export default class CategoryList extends React.Component {

  render() {
    let categories = [
      new FormLinkData("/Categories/ContactUs", "", "Contact Us"),
      new FormLinkData("/Categories/Search", "Search", "Search service requests by reference number"),
      new FormLinkData("/Categories/RoadsAndTraffic","Roads and traffic"),
      new FormLinkData("/Categories/BinsRecyclingAndUnwantedItems",  "Bin collections, recycling and unwanted items"),
      new FormLinkData("/Categories/Streets",  "Clean and clear streets"),
      new FormLinkData("/Categories/DogsAndPest",  "Dogs and pest control"),
      new FormLinkData("/Categories/Properties",  "Issues with commercial and residential properties"),
      new FormLinkData("/Categories/HealthAndSafety", "Health and safety"),
      new FormLinkData("/Categories/AntiSocialbehaviourAndNoise", "", "Antisocial behavior and noise nuisance"),
      new FormLinkData("/Categories/CouncilTax", "", "Council tax")
    ];

    return (
      <>
        <div class="text">
          <h1>On-line Services</h1>
        </div>

        <div class="groupPromo">
          <div class="listWebpart">
            <ul>
              {
                categories.map((element) => {
                  return (
                    <li>
                      <LccLinkTile to={element.to} category={element.category} header={element.header} description={element.description} />
                    </li>
                  );
                })
              }

            </ul>
          </div>
        </div>
      </>
    );
  }
}

class FormLinkData {
  constructor(to, header, description) {
    this.to = to;
    this.header = header;
    this.description = description;
  }
}
