import React from 'react';
import { Navigate } from "react-router-dom";
import getContactUsErrorList from 'data/ContactUs-ErrorList';
import withRouterHooks from 'helpers/withRouterHooks';

import TextInputLcc from 'views/components/inputs/Text';
import MultiCheckboxInputLcc from 'views/components/inputs/MultiCheckbox';
import RadioInputLcc from 'views/components/inputs/Radio';
import TextareaInputLcc from 'views/components/inputs/Textarea';
import DateInputLcc from 'views/components/inputs/Date';
import TimeInputLcc from 'views/components/inputs/Time';
import FileUploadInputLcc from 'views/components/inputs/FileUpload';
import DropdownInputLcc from 'views/components/inputs/Dropdown';
import CheckboxInputLcc from 'views/components/inputs/Checkbox';
import ParagraphLcc from 'views/components/inputs/Paragraph';
import ErrorSummaryLcc from 'views/components/display/ErrorSummary';
import HeaderLarge from 'views/components/display/HeaderLarge';

class PageBase extends React.Component {
  constructor(props) {
    super(props);
    //If controlled form will need all fields from json
    const [page] = this.props.outletContext;
    this.state = { FirstName: '', Surname: '', errors: [], page: page };

    this.handleChange = this.handleChange.bind(this);
  }

  //This will probably move
  handleChange(e) {
    //Lifting up - top level
    const target = e.target;
    const value = target.type === 'checkbox' ? target.checked : target.value;
    const name = target.name;
    this.setState({
      [name]: value
    });
  }

  validateForm = () => {
    let errors = [];
    let formIsValid = true;

    //Call a lambda instead here where pass form name and page and values
    //Will only bring for that page really
    let contactUsErrorList = getContactUsErrorList();
    formIsValid = contactUsErrorList.errorList.length > 0 ? false : true;
    errors = contactUsErrorList.errorList;
    this.setState({ errors });

    return formIsValid;
  };

  //This won't be here, just for dev purposes - will be on submit
  onCheckErrors = event => {
    event.preventDefault();
    if (this.validateForm()) {
      console.log("valid", this.state.errors);
    }
    else {
      console.log("invalid", this.state.errors);
    }

  };

  render() {
    let showQuestionLabel = this.state.page.questions.length > 1 || (this.state.page.questions[0].componentType === "Checkbox" ? false : true);
    showQuestionLabel = !showQuestionLabel
    if (this.state.page === undefined) {
      return <Navigate to="/ErrorNotFound" />;
    }

    const components = {
      Checkbox: CheckboxInputLcc,
      Text: TextInputLcc,
      MultiCheckbox: MultiCheckboxInputLcc,
      Radio: RadioInputLcc,
      Textarea: TextareaInputLcc,
      Date: DateInputLcc,
      Time: TimeInputLcc,
      FileUpload: FileUploadInputLcc,
      Dropdown: DropdownInputLcc,
      Paragraph: ParagraphLcc
    };

    return (
      <>
        {showQuestionLabel
          ? <HeaderLarge displayName={this.state.page.displayName} />
          : null}

        {(this.state.errors.length > 0)
          ? <ErrorSummaryLcc errors={this.state.errors} />
          : null}

        {this.state.page.questions.map((question) => {
          const ComponentType = components[question.componentType];
          let componentErrors = [];
          let thisQuestionsErrors = this.state.errors.filter(e => e.id === question.name);
          if (thisQuestionsErrors.length > 0) {
            componentErrors = thisQuestionsErrors[0].errors;
          }

          return (
            <div key={question.name + "FormGroup"} className={`form-group ${componentErrors.length > 0 ? "form-group-error" : ""}`}>
              <ComponentType qnData={question} showQuestionLabel={showQuestionLabel} errors={componentErrors} onValueChange={this.handleChange} />
            </div>
          );
        })
        }

        <div>
          <button className="btn btn-primary btn-small" id="TestErrors" onClick={this.onCheckErrors}> Check errors (for now) </button>
        </div>
      </>
    );
  }
}

export default withRouterHooks(PageBase);
