import React from 'react';
import ErrorMessageLcc from 'views/components/display/ErrorMessage';
import QuestionLabelLcc from '../display/QuestionLabel';

export default class DropdownInputLcc extends React.Component {
  render() {
    return (
      <>
        <QuestionLabelLcc qnData={this.props.qnData} qnCount={this.props.qnCount} />

        <ErrorMessageLcc errors={this.props.errors} />

        <select className={`form-control ${this.props.errors.length > 0 ? "form-control-error" : ""}`} id={this.props.qnData.name} name={this.props.qnData.name}>
          <option>{this.props.qnData.defaultOption}</option>
          {this.props.qnData.options.map((option) => {
            return (
              <option key={option.value} value={option.value} >{option.displayValue}</option>
            );
          })
          }
        </select>
      </>
    );
  }
}
