import React from 'react';
import { Outlet } from 'react-router-dom';
import withParamsAndNavigate from "Helpers/withParamsAndNavigate";

class FormBase extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      pageNumber: this.props.params.pageNumber,
      formName: this.props.params.formName,
    };

    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleBackButton = this.handleBackButton.bind(this);
  }

  render() {
    return (
      <>
        <br />
        <div className='form-group'>
          <button className='btn btn-info'>Back</button>
        </div>

        <form>
          <Outlet />

          <div className='form-group'>
            <button className='btn btn-success' type='submit'>Continue</button>
          </div>
        </form>
      </>
    );
  }

  handleSubmit(e) {
    e.preventDefault();
    let nextPage = this.getNextPage(this.state.pageNumber);
    this.setState({ pageNumber: nextPage });
    //this.props.navigate("/" + this.state.formName + "/" + nextPage);
    this.props.navigate(`/${this.state.formName}/${nextPage}`);

    //this.props.navigate("/" + this.state.formName + "/" + nextPage);
    //return false;
  };
}

export default withParamsAndNavigate(FormBase);
