import React, { Fragment } from 'react';
import withRouterHooks from "helpers/withRouterHooks";
import { Navigate } from "react-router-dom";
import Description from 'views/shared/Description';

class PageBase extends React.Component {

  render() {
    let formJson = require("../../forms/" + this.props.params.form + "/Config.json");
    let category = formJson.categories.find(category => category.name == this.props.params.page);

    if (category === undefined) {
      return <Navigate to="/ErrorNotFound" />;
    }

    return (
      category.questions.map((question) => {
        switch (question.type) {
          case "Description":
            return (
              <>
<Description header={question.header} />
              </>
              );

          case "Text":
            return (
              <Fragment key={question.name}>
                <label className='form-label'>{question.friendlyName}</label>
                <input className='form-control'></input>
              </Fragment>
            );

          default:
            break;
        }
      })

    );
  }

}

export default withRouterHooks(PageBase);
